#!/bin/sh
fontcustom compile build/img/svg -o build/fonts
mv -v build/fonts/fontcustom.css src/_styles/_components/_fontcustom.scss.tmp
sed -e "s/url(\"fontcustom-/url(\"..\/fonts\/fontcustom-/g" src/_styles/_components/_fontcustom.scss.tmp > src/_styles/_components/_fontcustom.scss
echo '/**\n* WARNING! THIS FILE WAS GENERATED BY FONT CUSTOM (https://github.com/endtwist/fontcustom/).\n* PLEASE DO NOT EDIT IT OR YOU WILL LOSE EVERYTHING.\n*/\n' | cat - src/_styles/_components/_fontcustom.scss > src/_styles/_components/_fontcustom.scss.tmp && mv src/_styles/_components/_fontcustom.scss.tmp src/_styles/_components/_fontcustom.scss