#!/bin/sh
rm src/assets/static/fonts/fontcustom-*
fontcustom compile src/assets/svg-to-fonts -o src/assets/static/fonts
mv -v src/assets/static/fonts/fontcustom.css src/assets/styles/_fontcustom.scss.tmp
sed -e "s/url(\"fontcustom-/url(\"..\/fonts\/fontcustom-/g" src/assets/styles/_fontcustom.scss.tmp > src/assets/styles/_fontcustom.scss
echo '/**\n* WARNING! THIS FILE WAS GENERATED BY FONT CUSTOM (https://github.com/endtwist/fontcustom/).\n* PLEASE DO NOT EDIT IT OR YOU WILL LOSE EVERYTHING.\n*/\n' | cat - src/assets/styles/_fontcustom.scss > src/assets/styles/_fontcustom.scss.tmp && mv src/assets/styles/_fontcustom.scss.tmp src/assets/styles/_fontcustom.scss